    INSERT INTO products (name, description, price, category_id, subcategory_id, published, featured, stock_quantity) VALUES
    ('ESCULTURA F1 LEWIS HAMILTON', 'Escultura em fibra de vidro de alta qualidade, com pneus de borracha e acabamento 3D. Pintura e verniz automotiva. Dimensões: 2,40m x 1,00m.', 18900.00, decoracao_category_id, esculturas_subcat_id, true, false, 1),
    ('ESCULTURA F1 AYRTON SENNA LOTUS 99T', 'Escultura em fibra de vidro de alta qualidade com pneus de borracha e acabamento 3D. Pintura e verniz automotiva. Dimensões: 2,20m x 1,00m.', 18900.00, decoracao_category_id, esculturas_subcat_id, true, false, 1),
    ('ESCULTURA F1 RED BULL MAX VERSTAPPEN', 'Escultura em fibra de vidro de alta qualidade, pneus de borracha e acabamento 3D, com pintura e verniz automotiva. Dimensões: 2,40m x 1,00m.', 18900.00, decoracao_category_id, esculturas_subcat_id, true, false, 1),
    ('FOREST KING', 'Escultura autoral de gorila em aço carbono resistente e sustentável, revestida com chapas de aço para alta durabilidade. Estilo figurativo geométrico. Dimensões: altura 1,00m, largura 62cm, comprimento 1,00m.', 22000.00, decoracao_category_id, esculturas_subcat_id, true, false, 1),
    ('LITTLE MONKEY GRAIN', 'Escultura de macaco-aranha feita em chapa de aço carbono 1,5mm, com acabamento primer e pintura automotiva semi brilho. Vários tamanhos e cores disponíveis sob encomenda. Dimensões: altura 1,00m, largura 56cm, comprimento 1,00m.', 20000.00, decoracao_category_id, esculturas_subcat_id, true, false, 1),
    ('BLACK PANTHER', 'Escultura metálica resistente da pantera negra em posição de ataque, feita em chapa de aço carbono 1,5mm, acabamento primer e pintura automotiva semi brilho. Disponível sob encomenda. Dimensões: altura 46cm, largura 39,8cm, comprimento 1,50m.', 22000.00, decoracao_category_id, esculturas_subcat_id, true, false, 1),
    ('BAD LION ll', 'Escultura autoral de leão sentado em chapa de aço carbono 1,5mm, com acabamento primer e pintura automotiva semi brilho. Peça resistente, disponível em vários tamanhos e cores. Dimensões: altura 1,00m, largura 56,5cm, comprimento 91cm.', 22000.00, decoracao_category_id, esculturas_subcat_id, true, false, 1),
    ('FINANCIAL BEAR', 'Escultura representando o urso financeiro, feito em chapa de aço carbono 1,5mm com acabamento primer e pintura automotiva semi brilho. Design moderno, ideal para ambientes corporativos. Dimensões: altura 1,50m, largura 79,5cm, comprimento 1,00m.', 24000.00, decoracao_category_id, esculturas_subcat_id, true, false, 1),
    ('FINANCIAL BULL', 'Escultura metálica do touro financeiro em chapa de aço carbono 1,5mm, acabamento primer e pintura automotiva semi brilho. Design arrojado com patas no chão e chifres erguidos. Dimensões: altura 1,00m, largura 64cm, comprimento 1,67m.', 24000.00, decoracao_category_id, esculturas_subcat_id, true, false, 1),
    ('JUNGLE RINO', 'Escultura moderna de rinoceronte feita em chapa de aço carbono 1,5mm, acabamento primer e pintura automotiva semi brilho. Peça autoral e resistente, disponível em vários tamanhos e cores. Dimensões: altura 70cm, largura 50cm, comprimento 1,30m.', 22000.00, decoracao_category_id, esculturas_subcat_id, true, false, 1),
    ('THE LEGEND - TWO COLORS', 'Escultura autoral de alce gigante (Megaloceros giganteus) em chapa de aço carbono 2mm, com acabamento primer e pintura automotiva semi brilho. Disponível sob encomenda. Dimensões: altura 1,50m, largura 1,30m, comprimento 1,17m.', 26000.00, decoracao_category_id, esculturas_subcat_id, true, false, 1),
    ('Miniatura Motor 250S Opala Escala 1:3 Com Base e Número de Série Único', 'Miniatura detalhada do motor 250S usado no Chevrolet Opala 6 cilindros (1968-1988). Peso 0,5 kg, dimensões base 30x20cm, motor 28x18x21cm. Produto personalizado com número de série único.', 1299.00, decoracao_category_id, esculturas_subcat_id, true, false, 1);
    INSERT INTO products (name, description, price, category_id, subcategory_id, published, featured, stock_quantity) VALUES
